import logo from './logo.svg';
import './App.css';
import React, {useState,useEffect}from 'react';
import axios from 'axios';

function App() {
  const [surn,setSurn] = useState([]);

  const getAl = ()=>{
    axios.get("http://localhost:3000/Perso").then(res=>{
      setSurn(res.data)
    })
  }

  useEffect(()=>{
    getAl()
  },[])

  const[datt,setDatt]=useState({
   name:"",
   surname:"",
   about:""
  })


  const addItem = ()=>{
    axios.post("http://localhost:3000/Perso",datt).then(res=>{
      console.log(res.data)
    })
  } 
   return (
     <div>
  {surn.map(i=>(
    <div cla key={i.id}> 
      <h1>{i.name}</h1>
      <h2>{i.surname}</h2>
      <p>{i.about}</p>
    </div>
  ))}
  <form onSubmit={addItem}>
    <input type="text" placeholder="name" onChange={(e)=>setDatt({...datt,name:e.target.value})} />
    <input type="text" placeholder="surname" onChange={(e)=>setDatt({...datt,surname:e.target.value})} />
    <input type="text" placeholder="Apropos"onChange={(e)=>setDatt({...datt,about:e.target.value})} />
 <input type="submit" />
 </form>
     </div>
   );

}



export default App;
