import './App.css';
import React, {useState,useEffect}from 'react';
import axios from 'axios';

function App() {
  const [surn,setSurn] = useState([]);

  const getAl = ()=>{
    axios.get("http://localhost:3000/Perso").then(res=>{
      setSurn(res.data)
    })
  }

  useEffect(()=>{
    getAl()
  },[])

  const[datt,setDatt]=useState({
   lname:"",
   fname:"",
   surname:"",
   age:"",
   about:""
  })


  const addItem = ()=>{
    axios.post("http://localhost:3000/Perso",datt).then(res=>{
      console.log(res.data)
    })
  } 
   return (
     <div className="App">

  {surn.map(i=>(
    <div className="car" key={i.id}> 
      <h1>{i.lname,i.fname}</h1>
      <h2>{i.surname}</h2>
      <h3>{i.age}</h3>
      <p>{i.about}</p>
    </div>
  ))}
 <form onSubmit={addItem}>
    <input type="text" placeholder="Nom" onChange={(e)=>setDatt({...datt,lname:e.target.value})} />
    <input type="text" placeholder="Prenom"onChange={(e)=>setDatt({...datt,fname:e.target.value})} />
    <input type="text" placeholder="Psudo" onChange={(e)=>setDatt({...datt,surname:e.target.value})} />
    <input type="text" placeholder="Age"onChange={(e)=>setDatt({...datt,age:e.target.value})} />
    <input type="text" placeholder="Apropos"onChange={(e)=>setDatt({...datt,about:e.target.value})} />
<input type="submit" />
 </form>
     </div>
   );

}



export default App;
