import './App.css';
import React, {useState,useEffect}from 'react';
import axios from 'axios';
import Update from './Update';

function App() {
  const [surn,setSurn] = useState([]);

  const getAl = ()=>{
    axios.get("http://localhost:3002/Perso").then(res=>{
      setSurn(res.data)
    })
  }

  useEffect(()=>{
    getAl();deleteItem()
  },[])

  const[datt,setDatt]=useState({
   lname:"",
   fname:"",
   surname:"",
   age:"",
   about:""
  })


  const addItem = ()=>{
    axios.post("http://localhost:3002/Perso",datt).then(res=>{
      console.log(res.data)
    })
  }
  const deleteItem = (id)=>{
    axios.delete(`http://localhost:3002/Perso/${id}`).then(res=>{
      console.log(res.data)
    })
  } 


   return (
     <div className="App">

       <div className="for">
      <form  onSubmit={addItem}>
        <input type="text" placeholder="Nom" onChange={(e)=>setDatt({...datt,lname:e.target.value})} />
        <input type="text" placeholder="Prenom"onChange={(e)=>setDatt({...datt,fname:e.target.value})} />
        <input type="text" placeholder="Psudo" onChange={(e)=>setDatt({...datt,surname:e.target.value})} />
        <input id="age"  type="number" placeholder="Age"onChange={(e)=>setDatt({...datt,age:e.target.value})} />
        <input type="text" placeholder="Apropos"onChange={(e)=>setDatt({...datt,about:e.target.value})} />
        <br/>
        <input id="sub" type="submit" />
      </form></div>

      <div className="bod"> 
  {surn.map(i=>(

    <div className="car" key={i.id}> 
    <div className="chead">
      <h1>{i.lname} {i.fname}</h1><hr/></div><hr/>
      <div className="cbody">
      <h2>{i.surname}</h2>
      <h3>{i.age}</h3>
      <p>{i.about}</p></div>
      <div className="cfoot">
      <button id="cbtn" onClick={()=>deleteItem(i.id)} > Delete</button>
     <Update i={i} id={i.id} /></div>
    </div>

  ))}
  </div>


     </div>
   );

}



export default App;
